import React, { Component,useState, useEffect} from 'react'
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import CveService from '../services/CveService';

class Linecharts extends Component {

     chartLabels = null;

    constructor(props){
        super(props)
        this.state = {
            cves:[],
            chartData: {
                labels: [chartLabels],
                datasets: [
                    {
                        label: 'Low',
                        data: [1, 2, 3, 4,5, 6],
                        fill: false,
                        backgroundColor: 'rgb(247, 240, 10)',
                        borderColor: 'rgba(247, 240, 10, 1)',
                    },
                    {
                        label: 'Medium',
                        data: [7, 8, 9, 10, 11, 12],
                        fill: false,
                        backgroundColor: 'rgb(14, 232, 36)',
                        borderColor: 'rgba(14, 232, 36, 1)',
                    },
                    {
                        label: 'High',
                        data: [13, 14, 15, 16, 17, 18],
                        fill: false,
                        backgroundColor: 'rgb(118, 11, 114)',
                        borderColor: 'rgba(118, 11, 114, 1)',
                    },
                    {
                        label: 'Critical',
                        data: [19, 20,21, 22, 23, 24],
                        fill: false,
                        backgroundColor: 'rgb(238, 7, 7)',
                        borderColor: 'rgba(128, 7, 7, 1)',
                    },
                ]
            }
        }
       
    }
    componentDidMount(){
        CveService.getMonthly().then((response) => {
            this.setState({ cves: response.data})
            this.state.cves.map(
                cve => 
                chartLabels=cve.monthYear
            )
        });
    }

  


    render() {
        return (
            <div className="chart">
                 <h1 className = "text-center">CVE Severities line chart</h1>
                <Line
                    data={this.state.chartData}
                    //   options={{maintainAspectRatio:false}}
                    options={{}}
                />
            </div>
        )
    }

}
export default Linecharts;
