import React, { Component,useState, useEffect} from 'react'
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import CveService from '../services/CveService';

class Linecharts extends Component {

    
    componentDidMount() {
        const [chartData, setChartData] = useState({});
        const [employeeSalary, setEmployeeSalary] = useState([]);
        const [employeeAge, setEmployeeAge] = useState([]);
        CveService.getYearly().then((response) => {
            this.setState({ cves: response.data })
        });
    }


    constructor(props) {
        

        super(props);
        this.state = {
            chartData: {
                labels: ['a', 'b', 'd', 'e', 'r', 'f'],
                datasets: [
                    {
                        label: 'Low',
                        data: [12, 19, 3, 5, 2, 3],
                        fill: false,
                        backgroundColor: 'rgb(247, 240, 10)',
                        borderColor: 'rgba(247, 240, 10, 1)',
                    },
                    {
                        label: 'Medium',
                        data: [5, 6, 3, 15, 12, 13],
                        fill: false,
                        backgroundColor: 'rgb(14, 232, 36)',
                        borderColor: 'rgba(14, 232, 36, 1)',
                    },
                    {
                        label: 'High',
                        data: [5, 6, 3, 15, 12, 13],
                        fill: false,
                        backgroundColor: 'rgb(118, 11, 114)',
                        borderColor: 'rgba(118, 11, 114, 1)',
                    },
                    {
                        label: 'Critical',
                        data: [15, 6, 3, 15, 12, 13],
                        fill: false,
                        backgroundColor: 'rgb(238, 7, 7)',
                        borderColor: 'rgba(128, 7, 7, 1)',
                    },
                ]
            }
        }
    }


    render() {
        return (
            <div className="chart">
                 <h1 className = "text-center">CVE Severities line chart</h1>
                <Line
                    data={this.state.chartData}
                    //   options={{maintainAspectRatio:false}}
                    options={{}}
                />
            </div>
        )
    }

}
export default Linecharts;
